function filterProducts(t,e){switch(e){case"greaterthan5":return t.filter((function(t){return t.price>5}));case"lesserthan5":return t.filter((function(t){return t.price<5}));case"equalto5":return t.filter((function(t){return 5===t.price}));default:return t}}function renderProducts(t){t.forEach((function(t){$(`<div class="col-md-3">\n    <div class="item">\n      <img src="/dist/img/${t.img}" alt="" />\n      <h2>${t.title}</h2>\n      <div class="flex j-between">\n        <span>$${t.price}</span>\n        <span>Stock: ${t.stock}</span>\n      </div>\n    </div>\n  </div>`).appendTo(".list")}))}function emptyProducts(){$(".list").empty()}function resetInput(){$(".title").val(""),$(".img").val(""),$(".stock").val(""),$(".price").val("")}$((function(){const t=[{id:1,title:"ship nam",img:"item.jpg",price:10,stock:9},{id:2,title:"ship nU",img:"item.jpg",price:12,stock:0},{id:3,title:"trai caY",img:"item.jpg",price:3,stock:0},{id:4,title:"Banh bao",img:"item.jpg",price:5,stock:2},{id:5,title:"hai san",img:"item.jpg",price:12,stock:1},{id:6,title:"This is a title 6",img:"item.jpg",price:150,stock:6},{id:7,title:"This is a title 7",img:"item.jpg",price:23,stock:1}];renderProducts(t),$("input.search").keyup((function(e){e.preventDefault();let i=$(this).val();const n=t.filter((function(t){return t.title.toLowerCase().includes(i.toLowerCase())}));emptyProducts(),renderProducts(n)})),$("button.add").click((function(e){e.preventDefault();const i=$(".title").val(),n=$(".img").val(),r=$(".stock").val(),c=$(".price").val(),s={id:t.length+1,title:i,img:n,stock:r,price:c};t.push(s),emptyProducts(),renderProducts(t),resetInput()})),$("#filter").change((function(e){e.preventDefault(),emptyProducts(),renderProducts(filterProducts(t,$(this).val()))}))}));